void Pitch::UpdateStatus(){
		std::string limitOutput = "";
	
	if (limitSwitchMax->Get())
		limitOutput = "Maximum Height";
	else if (limitSwitchMin->Get())
		limitOutput = "Minimum Height";
	else
		limitOutput = "";
	
	SmartDashboard::PutString("Pitch Height", limitOutput);
}
